import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";
import { toast } from "react-hot-toast";

const Cart = () => {
  const { cart } = useSelector((state) => state);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  const handleCheckout = () => {
    // Perform any necessary checkout logic here

    // Display a thank you message toast
    console.log("order details", cart);
    toast.success("Thank you for placing your order!");
  };

  return (
    <div style={{ display: "flex", flexWrap: "wrap" }}>
      <div
        id="1"
        style={{
          flex: "1",
          minWidth: "300px",
          padding: "20px",
          boxSizing: "border-box",
        }}
      >
        <div className=" h-40px w-50px">
          {cart.map((item, index) => {
            return <CartItem key={item.id} item={item} itemIndex={index} />;
          })}
        </div>
      </div>

      <div
        id="2"
        style={{
          flex: "1",
          minWidth: "300px",
          padding: "20px",
          boxSizing: "border-box",
          position: "fixed",
          top: "0",
          right: "0",
          zIndex: 1,
        }}
      >
        <div>
          <div backgroundColor="green" width="250px">
            <div
              style={{
                fontWeight: "bold",
                fontSize: "20px",
                marginBottom: "20px",
                color: "green",
              }}
            >
              Your Cart
            </div>
            <div
              style={{
                fontWeight: "bold",
                fontSize: "26px",
                marginBottom: "20px",
                color: "green",
              }}
            >
              Summary
            </div>
            <p style={{ marginBottom: "20px" }}>
              <span>Total Items: {cart.length}</span>
            </p>
            <div className="">
              <p style={{ fontWeight: "bold", fontSize: "20px", marginBottom: "20px" }}>
                Total Amount: ${totalAmount}
              </p>
              <button
                onClick={handleCheckout}
                style={{
                  backgroundColor: "green",
                  color: "white",
                  padding: "10px 20px",
                  borderRadius: "5px",
                  fontSize: "16px",
                  cursor: "pointer",
                }}
              >
                Check Out Now
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;
