import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";
import { toast } from "react-hot-toast";
import db from "../pages/firebase";// Import the Firebase instance you created

const Cart = () => {
  const { cart } = useSelector((state) => state);
  const [totalAmount, setTotalAmount] = useState(0);
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  const handleCheckout = async (e) => {
    // Extract title and price from cart items 
    e.preventDefault();
    console.log(cart);
   
    const itemData = cart.map((item) => {
      return {
        title: item.title,
        price: item.price,
      };
    });
    console.log("itemData",itemData);

    const options={
      method: 'POST',
      headers: {
        'Content-Type' : 'aplication/json'
      },
      body: JSON.stringify({
        name,phone,itemData
      })
    }
     const res=fetch('https://fashionnova-7b212-default-rtdb.asia-southeast1.firebasedatabase.app/UserData.json',options)
    if(res){
      alert("Message sent")
    }else{
       alert("error occured")
    }
  };

  return (
    <div>
      {/* ... Your existing cart rendering code ... */}
      <form onSubmit={handleCheckout}>
        <label>
          Name:
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </label>
        <label>
          Phone:
          <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} />
        </label>
        <button type="submit">Checkout</button>
      </form>
    </div>
  );
};

export default Cart;
