import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";
import { toast } from "react-hot-toast";

const Cart = () => {
  const { cart } = useSelector((state) => state);
  const [totalAmount, setTotalAmount] = useState(0);
  const [isLoading, setIsLoading] = useState(true); // Add loading state

  useEffect(() => {
    // Simulate loading for 2 seconds
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);

    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  const handleCheckout = () => {
    // Perform any necessary checkout logic here

    // Display a thank you message toast
    toast.success("Thank you for placing your order!");
  };

  return (
    <div>
      {isLoading ? ( // Show loader while isLoading is true
        <div
          style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
          }}
        >
          {/* Add your loader component here */}
          Loading...
        </div>
      ) : cart.length > 0 ? (
        <div style={{ display: "flex" }}>
          <div
            id="1"
            style={{ flex: "0.6", paddingLeft: "20px", paddingTop: "20px" }}
          >
            <div className=" h-40px w-50px">
              {cart.map((item, index) => {
                return <CartItem key={item.id} item={item} itemIndex={index} />;
              })}
            </div>
          </div>

          <div id="2" style={{ flex: "0.3", position: "fixed", top: "200px",left:"150vh" ,zIndex: 1 }}>
            {/* ...Your Cart and Summary content */}
          </div>
        </div>
      ) : (
        <div>
          <h1>Your Cart is Empty</h1>
          <Link to={"/"} style={{ textDecoration: "none" }}>
            <button
              style={{
                backgroundColor: "green",
                color: "white",
                padding: "10px 20px",
                borderRadius: "5px",
                fontSize: "16px",
                cursor: "pointer",
              }}
            >
              Shop Now
            </button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default Cart;
