import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";

const cartItemStyle = {
  display: "flex",
  alignItems: "center",
  marginBottom: "20px",
  width: "50%", // Set the width to 50%
};
const cartHeaderStyle = {
  fontWeight: "bold",
  fontSize: "24px",
  marginBottom: "16px",
};

const cartSummaryStyle = {
  fontWeight: "bold",
  fontSize: "20px",
  marginBottom: "16px",
};

const cartTotalStyle = {
  fontWeight: "bold",
  fontSize: "20px",
};

const checkoutButtonStyle = {
  backgroundColor: "blue",
  color: "white",
  padding: "10px 20px",
  borderRadius: "5px",
  fontSize: "16px",
  cursor: "pointer",
};

const Cart = () => {
  const { cart } = useSelector((state) => state);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  return (
    <div style={cartContainerStyle}>
      {cart.length > 0 ? (
        <div>
          <div className="bg-slate-900 h-40px w-50px">
            {cart.map((item, index) => {
              return <CartItem key={item.id} item={item} itemIndex={index} />;
            })}
          </div>

          <div backgroundColor="green" width="250px">
            <div style={cartHeaderStyle}>Your Cart</div>
            <div style={cartSummaryStyle}>Summary</div>
            <p>
              <span>Total Items: {cart.length}</span>
            </p>
          </div>

          <div className="">
            <p style={cartTotalStyle}>Total Amount: ${totalAmount}</p>
            <button style={checkoutButtonStyle}>Check Out Now</button>
          </div>
        </div>
      ) : (
        <div>
          <h1>Your Cart is Empty</h1>
          <Link to={"/"} style={{ textDecoration: "none" }}>
            <button style={checkoutButtonStyle}>Shop Now</button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default Cart;
