import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";
import { toast } from "react-hot-toast";
import db from "../pages/firebase";// Import the Firebase instance you created

const Cart = () => {
  const { cart } = useSelector((state) => state);
  const [totalAmount, setTotalAmount] = useState(0);
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  const handleCheckout = async () => {
    // Extract title and price from cart items
    const itemData = cart.map((item) => {
      return {
        title: item.title,
        price: item.price,
      };
    });
    console.log(itemData);
    // Create a Firestore document for the user's details
    try {
      const userRef = await db.collection('users').add({
        name,
        phone,
      });

      // Get the ID of the newly created user document
      const userId = userRef.id;

      // Prepare cart data with the user's ID
      const cartWithUserId = cart.map((item) => ({
        ...item,
        userId,
      }));

      // Create a Firestore document for the cart data
      await db.collection('carts').add({
        userId,
        items: cartWithUserId,
      });

      // Reset form fields
      setName('');
      setPhone('');

      // Handle success, e.g., redirect to a thank-you page
      toast.success("Thank you for placing your order!");
    } catch (error) {
      console.error('Error submitting form:', error);
      // Handle errors, e.g., display an error message to the user
      toast.error("An error occurred while processing your order. Please try again later.");
    }
  };

  return (
    <div>
      {/* ... Your existing cart rendering code ... */}
      <form onSubmit={handleCheckout}>
        <label>
          Name:
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </label>
        <label>
          Phone:
          <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} />
        </label>
        <button type="submit">Checkout</button>
      </form>
    </div>
  );
};

export default Cart;
