import React, { useState } from "react";
import { useSelector } from "react-redux";

const Form = () => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const { cart } = useSelector((state) => state);

  const handleSubmit =async (e) => {
    e.preventDefault();

    if (name && phone && address) {
        console.log(cart);
   
    const itemData = cart.map((item) => {
      return {
        title: item.title,
        price: item.price,
      };
    });
    console.log("itemData",itemData);

    const options={
      method: 'POST',
      headers: {
        'Content-Type' : 'aplication/json'
      },
      body: JSON.stringify({
        name,phone,itemData
      })
    }
     const res=await fetch('https://fashionnova-7b212-default-rtdb.asia-southeast1.firebasedatabase.app/UserData.json',options)
    if(res){
      alert("Message sent")
    }else{
       alert("error occured")
    }
      // Submit the data to your desired location (e.g., Firebase) here
      console.log("Data submitted:", { name, phone, address });
    } else {
      // Display an error message or handle incomplete data
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <label>
          Phone:
          <input
            type="text"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </label>
        <label>
          Address:
          <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
        </label>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Form;
