import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";
import { toast } from "react-hot-toast";

const Cart = () => {
  const { cart } = useSelector((state) => state);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  const handleCheckout = () => {
    // Perform any necessary checkout logic here

    // Display a thank you message toast
    toast.success("Thank you for placing your order!");
  };

  return (
    <div>
      {cart.length > 0 ? (
        <div id="4" style={{ display: "flex" }}>
          <div id="1">
            {/* ... */}
          </div>

          <div id="2">
            <div backgroundColor="green" width="250px">
              <div style={{ fontWeight: "bold", fontSize: "24px", color: "white" }}>
                Your Cart
              </div>
              <div style={{ fontWeight: "bold", fontSize: "20px" }}>
                Summary
              </div>
              <p>
                <span>Total Items: {cart.length}</span>
              </p>
              <div className="">
                <p style={{ fontWeight: "bold", fontSize: "20px" }}>
                  Total Amount: ${totalAmount}
                </p>
                <button
                  onClick={handleCheckout}
                  style={{
                    backgroundColor: "green", // Set button background color to green
                    color: "white", // Set button text color to white
                    padding: "10px 20px",
                    borderRadius: "5px",
                    fontSize: "16px",
                    cursor: "pointer",
                  }}
                >
                  Check Out Now
                </button>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div>
          <h1>Your Cart is Empty</h1>
          <Link to={"/"} style={{ textDecoration: "none" }}>
            {/* ... */}
          </Link>
        </div>
      )}
    </div>
  );
};

export default Cart;
